import React, { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import InventoyHeader from "../InventoryHeader/InventoyHeader.js";
import axios from "axios";

const IURL = "http://Localhost:5001/inventory";

const fetchInventory = async () => {
  return await axios.get(IURL).then((res) => res.data);
};

function InventoryHome() {
  const [inventory, setInventory] = useState();
  useEffect(() => {
    fetchInventory().then((data) => setInventory(data.inventory));
  }, []);

  return (
    <div>
      <InventoyHeader />
      <div className="flex flex-col gap-5 p-5 m-10 w-72 mx-auto">
        <Link to="/addinventory">
          <button className="bg-lime-700 text-white p-3 rounded-lg uppercase w-72 over:opacity-95">
            + Add New Inventory
          </button>
        </Link>
      </div>
      <div>
        {inventory &&
          inventory.map((inventory, i) => (
            <div key={i}>
              <AddNewInventory inventory={inventory} />
            </div>
          ))}
      </div>
    </div>
  );
}
export default InventoryHome;
