import React, { useEffect } from "react";
import { useState } from "react";
import InventoyHeader from "../InventoryHeader/InventoyHeader";
import axios from "axios";
import Inventory from "../InventoryList/Inventory";

const IURL = "http://Localhost:5001/inventory";

const fetchInventory = async () => {
  return await axios.get(IURL).then((res) => res.data);
};

function InventoryDetails() {
  const [inventory, setInventory] = useState();
  useEffect(() => {
    fetchInventory().then((data) => setInventory(data.inventory));
  }, []);
  return (
    <div>
      <InventoyHeader />
      <h1 className="text-center font-semibold m-2">
        Inventory Details Display
      </h1>
      <hr className="border-2" />

      <div className="mt-10">
        <table className="border-2 mx-auto">
          <tr className="bg-green-500 border-green-800">
            <th className="border-2 p-2 w-48">Product Name</th>
            <th className="border-2 p-2 w-48">Product Category</th>
            <th className="border-2 p-2 w-48">Material Type</th>
            <th className="border-2 p-2 w-24">Quantity</th>
            <th className="border-2 p-2 w-48"> Product Description</th>
            <th className="border-2 p-2 w-52">Actions</th>
          </tr>
        </table>
      </div>

      {/*get the inventroy details repetitively from the Inventory.js*/}
      {inventory &&
        inventory.map((inventory, i) => (
          <div key={i}>
            <Inventory inventory={inventory} />
          </div>
        ))}
    </div>
  );
}

export default InventoryDetails;
